query GetPages($first: Int, $after: String) {
  pages(first: $first, after: $after) {
    edges {
      node {
        url
        ... on WikidotPage {
          url
                alternateTitles
                wikidotId
                title
                rating
                voteCount
                category
                tags
                createdAt
                revisionCount
        }
      }
      cursor
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}

query GetUser($displayName: String!) {
  wikidotUser(displayName: $displayName) {
    displayName
    wikidotId
    unixName
  }
}
